    INTO var_job_id, var_schedule_id, var_step_name, var_step_id  
   WHERE jst.job_step_id = var_job_step_id;
  */
  INSERT 
    INTO aws_sqlserver_ext.sysjobhistory (
         job_id
       , step_id
