    calendaryear, calendarquarter, SUM(incomevalue) AS incomevalue&#x000d;
    FROM dbo.factincome AS f&#x000d;
    INNER JOIN dbo.dimdate AS d&#x000d;
        ON f.dateid = d.dateid&#x000d;
    GROUP BY calendaryear, calendarquarter;

