)&#x000d;
*/&#x000d;
DECLARE&#x000d;
    var_Value VARCHAR(256);&#x000d;
BEGIN&#x000d;
    var_Value := (SELECT&#x000d;
        strvalue&#x000d;
        FROM meta.configapp&#x000d;
        WHERE LOWER(parameter) = LOWER(par_Parameter));&#x000d;
    RETURN var_Value;&#x000d;
END;

